import React, { useState } from 'react'
import { InputItem, Toast } from 'antd-mobile'
import styles from './index.module.css'

const ManageLogin = () => {
  // 手机号是否错误
  const [errPhone, setErrPhone] = useState(false)
  // 密码是否错误
  const [errPsd, setErrPsd] = useState(false)
  // 参数
  const [formAlign, setFormAlign] = useState({
    phone: '',
    password: ''
  })
  // 手机号错误
  const onChange1 = (value) => {
    if (value.replace(/\s/g, '').length < 11) {
      setErrPhone(true)
    } else {
      setErrPhone(false)
    }
    setFormAlign({...formAlign, phone: value})
  }
  // 密码错误
  const onChange2 = (value) => {
    if (!(/^[\d\w]{6,12}$/.test(value))) {
      setErrPsd(true)
    } else {
      setErrPsd(false)
    }
    setFormAlign({...formAlign, password: value})
  }
  return (
    <div>
      <InputItem
        type="phone"
        placeholder="请输入账号"
        error={errPhone}
        onErrorClick={() => {
          if (errPhone) Toast.info('请输入正确的手机号')
        }}
        onChange={onChange1}
        value={formAlign.phone}
      >账号</InputItem>
      <div style={{height: '10px'}}></div>
      <InputItem
        placeholder="请输入密码"
        error={errPsd}
        onErrorClick={() => {
          if (errPsd) Toast.info('请输入正确的密码')
        }}
        onChange={onChange2}
        value={formAlign.password}
      >密码</InputItem>
    </div>
  )
}

export default ManageLogin
