import React, { useState } from 'react'
import { InputItem, Toast, Button } from 'antd-mobile'
import axios from '../../../untils/axios'

const ManageLogin = () => {
  // 手机号是否错误
  const [errPhone, setErrPhone] = useState(false)
  // 密码是否错误
  const [errPsd, setErrPsd] = useState(false)
  // 参数
  const [formAlign, setFormAlign] = useState({
    phone: '',
    password: ''
  })
  // 手机号错误
  const onChange1 = (value) => {
    if (value.replace(/\s/g, '').length < 11) {
      setErrPhone(true)
    } else {
      setErrPhone(false)
    }
    setFormAlign({...formAlign, phone: value})
  }
  // 密码错误
  const onChange2 = (value) => {
    if (!(/^[\d\w]{6,12}$/.test(value))) {
      setErrPsd(true)
    } else {
      setErrPsd(false)
    }
    setFormAlign({...formAlign, password: value})
  }
  // 登录
  const submit = async () => {
    console.log(formAlign)
    if ((!(/^[\d\w]{6,12}$/.test(formAlign.password))) || (!(/^1[3456789]d{9}$/.test(formAlign.phone)))) return Toast.fail('请输入正确的账号和密码')
    const { data: res } = await axios.post('', formAlign)
    console.log(res)
  }
  return (
    <div style={{overflow: 'hidden'}}>
      <h3 style={{margin: '40px 0', textAlign: 'center'}}>重点汽车网经销商登录</h3>
      <InputItem
        placeholder="请输入账号"
        onChange={value => setFormAlign({...formAlign, phone: value})}
        value={formAlign.phone}
      >账号</InputItem>
      <div style={{height: '10px'}}></div>
      <InputItem
        placeholder="请输入密码"
        onChange={value => setFormAlign({...formAlign, password: value})}
        value={formAlign.password}
      >密码</InputItem>
      <div style={{height: '30px'}}></div>
      <Button type="primary" onClick={submit}>登录</Button>
    </div>
  )
}

export default ManageLogin
