import React from 'react'
// axios
import axios from '../../../untils/axios'
import styles from './index.module.css'

export default class ManageCustomer extends React.Component {
  constructor (props) {
    super(props)
    this.state = {
      list: [], // 数据
      pageNo: 1, // 分页
      isLoadingMore: false // 是否加载更多
    }
  }
  componentDidMount () {
    // 获取首页列表数据
    this.getHomeList()
    // 监听滚动事件
    const wrapper = this.refs.wrapper
    const loadMoreDataFn = this.loadMoreDataFn
    const that = this
    let timeCount
    function callback () {
      const top = wrapper.getBoundingClientRect().top // 加载更多距离顶部的距离
      const windowHeight = window.screen.height // 屏幕可视区域的高度
      if (top && top < windowHeight) {
        // 当 wrapper 已经被滚动到页面可视范围之内触发
        loadMoreDataFn(that)
      } 
    }
    window.addEventListener('scroll', () => {
      if (this.state.isLoadingMore) return
      if (timeCount) {
        clearTimeout(timeCount)
      }
      timeCount = setTimeout(callback, 500)
    }, false)
  }
  // 加载更多事件
  async loadMoreDataFn (that) {
    await that.setState({ pageNo: that.state.pageNo + 1 })
    const { data: res } = await axios.post('/keyN/home', {
      pageNo: that.state.pageNo,
      pageSize: 10
    })
    that.setState({ list: [...that.state.list,...res] })
  }
  // 获取列表数据
  async getHomeList () {
    const { data: res } = await axios.post('/keyN/home', {
      pageNo: this.state.pageNo,
      pageSize: 10
    })
    this.setState({ list: res })
  }
  // 监听滚动事件
  bindHandleScroll =() => {
    let winHeight = Math.ceil(window.scrollY + window.innerHeight)
    return winHeight >= document.body.scrollHeight - 50 // 距离底部为50时候发送请求
  }
  render () {
    return (
      <div>
        {/* 首页列表 */}
        <ul className={styles.inforList}>
          {
            this.state.list.map(item => {
              return <li key={item.id}>
                <h1>11111</h1>
              </li>
            })
          }
        </ul>
        <div
          className={styles.loadMore}
          ref="wrapper"
          onClick={this.loadMoreDataFn.bind(this, this)}
        >
        </div>
      </div>
    )
  }
}
