import React, { useState } from 'react'
import { InputItem, Toast } from 'antd-mobile'

const ManageLogin = () => {
  // 手机号错误
  const [errPhone, setErrPhone] = useState(false)
  // 参数
  const [formAlign, setFormAlign] = useState({
    phone: '',
    password: ''
  })
  const onErrorClick = () => {
    if (errPhone) Toast.info('请输入正确的手机号')
  }
  const onChange = (value) => {
    if (value.replace(/\s/g, '').length < 11) {
      setErrPhone(true)
    } else {
      setErrPhone(false)
    }
    setFormAlign({...formAlign, phone: value})
  }
  return (
    <div>
      <InputItem
        type="phone"
        placeholder="请输入手机号"
        error={errPhone}
        onErrorClick={onErrorClick}
        onChange={onChange}
        value={formAlign.phone}
      >手机号码</InputItem>
      <InputItem
        type="phone"
        placeholder="请输入手机号"
        error={errPhone}
        onErrorClick={onErrorClick}
        onChange={onChange}
        value={formAlign.phone}
      >手机号码</InputItem>
    </div>
  )
}

export default ManageLogin
